  return(

<div>

<h1>Колекции</h1>

<div className="container-all" id = "maincontent">

<input type="text" required className="form-control" name="colname"
  placeholder="Введите название новой колекции"
  value={this.state.colname}
  onChange={this.handleUserInput}  />

  <button onClick={this.addcoll}> Добавить колекцию! </button>

<br/>

{
this.state.pics.map((image, index) => {
  return (
    <div className="forcol">
    <h1>{image[0].collection}</h1>
    <button onClick={() => this.delete(image[0].collection, index)}> Удалить колекцию </button>
    <button onClick={this.deleteALL}> Удалить колекцию со всеми картинками! </button>

    <br/>
  {
    image.map((data) => {
          return(
  <div class = 'container'>
  <a href={'/' + data.id}>
  <img src={'data:image/' + data.type +';base64,' + btoa(data.value)} alt='' />
  </a>
  <span class='title'>{data.name}</span>
  <span class='text'>{data.desc}</span>
  </div>
)})
}
<br/>
</div>

)
}
)
}


</div>
</div>

);
}

}

export default Collections;
