import React from 'react';
import './home.css';
import IndexedDbRepository from '../db/connection';


class Collections extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
        pics: [],
        readypics: [],
        colname: null,
        collections: []
    };
    var temp, dbtest;
  }




  componentDidMount() {

    let statepics = [];
    this.dbtest = new IndexedDbRepository( 'Storage' );

    if(localStorage.getItem('collections') === null)
    {
      var colls = [null];
      localStorage.setItem("collections", JSON.stringify(colls));
      this.setState({collections: JSON.parse(localStorage.getItem("collections")) });
    }
    else
    {
      this.setState({collections: JSON.parse(localStorage.getItem("collections")) });
    }

    alert(this.state.collections.length);

    for (var i = 1, len = this.state.collections.length; i < len; i++) {
    alert("aaa");
        this.dbtest.findByIndex("collection", this.setState.collections[i]).then(pictures =>
        this.setState( { pics: this.state.pics.push(pictures)}));
        console.log(this.state.pics);
  }





    // this.dbtest.findByIndex("tags", "котики").then( pictures => this.setState( { pics: pictures } ) );
    }


    handleUserInput = (e) => {
      const value = e.target.value;
      this.setState({colname: value});
    }

    addcoll = () =>
    {
      if(this.state.colname !== "" && this.state.colname !== "null")
      {
        var temp;
        temp = JSON.parse(localStorage.getItem("collections"));

        if(temp.includes(this.state.colname))
        {
          alert("Колекция с таким именем уже существует!");
        }
        else
        {
          console.log(temp);
          temp.push(this.state.colname);
          console.log(temp);
          localStorage.setItem("collections", JSON.stringify(temp));
          this.setState({collections: JSON.parse(localStorage.getItem("collections")) });
        }



      }
    }





  render() {

    const myData = this.state;

    if(!Array.isArray(this.state.pics))
    {
      this.state.pics  = [this.state.pics];
    }

  return(

<div>

<h1>Чудо-галерея</h1>

<div className="container-all" id = "maincontent">

<input type="text" required className="form-control" name="colname"
  placeholder="Введите название новой колекции"
  value={this.state.colname}
  onChange={this.handleUserInput}  />

  <button onClick={this.addcoll}> Добавить колекцию! </button>

<br/>



{
this.state.pics.map((pics) => {

          return(

  <div class = 'container'>
  <a href={'/' + pics.id}>
  <img src={'data:image/' + pics.type +';base64,' + btoa(pics.value)} alt='' />
  </a>
  <span class='title'>{pics.name}</span>
  <span class='text'>{pics.desc}</span>
  </div>
)})
}

</div>
</div>

);
}

}

export default Collections;
