import React from 'react';
import './home.css';
import IndexedDbRepository from '../db/connection';


class Home extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
        pics: [],
        readypics: [],
        search: this.props.search,
        searchtype: this.props.searchingtype,
        searchtag: this.props.tagbool
    };
    var db, dbtest;
  }




  componentDidMount() {

    let statepics = [];
    this.dbtest = new IndexedDbRepository( 'Storage' );
    this.dbtest.findAll().then( pictures => this.setState( { pics: pictures } ) );

    navigator.storage.estimate().then(function(estimate) {
    console.log(estimate.usage);
    console.log(estimate.quota);

  });

  if(this.state.searchtype === "tags")
  {
    this.search();
  }

    // this.dbtest.findByIndex("tags", "котики").then( pictures => this.setState( { pics: pictures } ) );
    }


    handleUserInput = (e) => {
      const value = e.target.value;
      this.setState({search: value});
    }

    search = () =>
    {
      if(this.state.search === "")
      {
        this.dbtest.findAll().then( pictures => this.setState( { pics: pictures } ) );
      }

      else {
      this.dbtest.findByIndex(this.state.searchtype, this.state.search).then( pictures => this.setState( { pics: pictures } ) );
    }
    }


      colchange = (e) =>
      {

        this.setState({searchtype: e.target.value});
      }





  render() {

    const myData = this.state;

    if(!Array.isArray(this.state.pics))
    {
      this.state.pics  = [this.state.pics];
    }

  return(

<div>

<h1>Чудо-галерея</h1>

<div id = "maincontent">

<div className = "inputfield">
<input type="text"  className="searchinput" name="name"
  placeholder="Начать поиск"
  value={this.state.search}
  onChange={this.handleUserInput}  />

  <select onChange = {this.colchange} className = "selectinput">
        <option value = "name">По названию</option>
        <option value = "tags" selected = {this.state.searchtag}>По тегу</option>
        <option value = "collection">По коллекции</option>
  </select>


</div>

  <div className = "inputfield">
  <button className = "searchbutton" onClick={this.search}> Поиск! </button>
  </div>

<br/>


<div className="gallery">
   <ul>

{
this.state.pics.map((pics) => {

          return(

//  <div class = 'container'>
//
//  <img src={'data:image/' + pics.type +';base64,' + btoa(pics.value)} alt='' />
//
//  <span class='title'>{pics.name}</span>
//  <span class='text'>{pics.desc}</span>
//  </div>


      <div class = 'container'>
      <li className = "galli"><a href={'/' + pics.id}><img src={'data:image/' + pics.type +';base64,' + btoa(pics.value)} alt='' /></a>
      <span class='title'>{pics.name}</span>
      <span class='text'>{pics.desc}</span>
</li>
</div>

)})
}

</ul>
</div>



</div>
</div>

);
}

}

export default Home;
