import React from 'react';
import './home.css';

import IndexedDbRepository from '../db/connection';


class AddPicture extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
      image: false,
      urls: [],
      preview: "https://icon-library.net/images/upload-photo-icon/upload-photo-icon-21.jpg"
    };

    this.uploadpicture = this.uploadpicture.bind(this);
    this.handlePicUpload = this.handlePicUpload.bind(this);

    var db, dbtest;
    var uploadingPicture;
  }


  handlePicUpload = (e) => {

    this.uploadingPicture =
{
    name: null,
    format: null,
    width: null,
    height: null,
    size: null,
    type: null,
    date: null,
    desc: "test",
    tags: "котики тест проверка",
    value: null
};


  const value = e.target.value;
  var file = e.target.files[0];

  if (file.type.split('/')[0] != 'image' )
  {
    this.state.image = false;
    alert("Неправильный формат файла!!!");
  }

  else {

  this.setState({
  preview: URL.createObjectURL(file)
  });

  this.state.image = true;
  this.uploadingPicture.type = file.type.split('/')[1];

  var sizeInMB = (file.size / (1024*1024)).toFixed(2);
  this.uploadingPicture.size = sizeInMB;


  var _URL = window.URL || window.webkitURL;

  var img;
  img = new Image();
  var objectUrl = _URL.createObjectURL(file);
  img.src = objectUrl;



  img.onload = () => {

      this.uploadingPicture.width = img.width;
      this.uploadingPicture.height = img.height;

      if(img.height > img.width)
      {
        this.uploadingPicture.format = "portrait";
      }
      else if(img.width > img.height)
      {
        this.uploadingPicture.format = "album";
      }
      else {this.uploadingPicture.format = "square"}
      _URL.revokeObjectURL(objectUrl);
      console.log(this.uploadingPicture);
  };
}
}

handleUserInput = (e) => {
  const name = e.target.name;
  const value = e.target.value;
  this.setState({[name]: value});
}




  componentDidMount() {

    this.dbtest = new IndexedDbRepository( 'Storage' );
    console.log(this.db);
    }



    uploadpicture()
    {

      if(this.state.image === true) {
    alert(this.dbtest);

		  	let file = this.state.img;
        alert(file);

				var reader = new FileReader();

				reader.readAsBinaryString(file);

				reader.onload = (e) => {

					let bits = e.target.result;
        // uploadingPicture.value = bits;


					let ob = bits;
          this.dbtest.save( { value: bits } );

				}
}
else {alert("Загрузите, пожалуйста, картинку!");}
    }


  render() {



  return(

<div>

<h1>Добавить картинку</h1>

<div className="container-all" id = "maincontent">

  <div className = "uploadingpicture">
    <label for="picture">
    <img src={this.state.preview}/>
    </label>

<input id="picture" type="file" accept="image/*" onChange={this.handlePicUpload} />
  </div>

  <div>
  Title
  <input type="text" required className="form-control" name="title"
    placeholder="Заголовок статьи"
    value={this.state.title}
    onChange={this.handleUserInput}  />
</div>
<br />
<div>
  Text
  <textarea type="textarea" height="300px" className="form-control bigtext" name="maintext"
    placeholder="Содержимое статьи"
    value={this.state.maintext}
    onChange={this.handleUserInput}  />
</div>

</div>

<div>
</div>

<button onClick={this.uploadpicture}>  Активировать лазеры

</button>



</div>

);
}

}

export default AddPicture;
