import React from 'react';
import './App.css';

import Home from './pages/home'

import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';

function App() {

  let db;
  let dbVersion = 1;
  let dbReady = false;

  let request = indexedDB.open('TestCollection', dbVersion);

  request.onerror = function(e) {
    console.error('Unable to open database.');
  }

  request.onsuccess = function(e) {
    db = e.target.result;
    console.log('db opened ' + db.name);
  }

  request.onupgradeneeded = function(e) {
        let db = e.target.result;
        var objectStore = db.createObjectStore('Pictures', {keyPath:'id', autoIncrement: true});

        objectStore.createIndex("format", "format", { unique: false });
        objectStore.createIndex("height", "height", { unique: false });
        objectStore.createIndex("size", "size", { unique: false });
        objectStore.createIndex("type", "type", { unique: false });
        objectStore.createIndex("date", "date", { unique: false });
        objectStore.createIndex("desc", "desc", { unique: false });
        objectStore.createIndex("tags", "tags", { unique: false });
        objectStore.createIndex("value", "value", { unique: false });
        dbReady = true;
      }

    return (

<Router>
<div>
        <Switch>
          <Route path="/" exact component={Home} />
        </Switch>

  </div>
</Router>
    );

  }

export default App;
