import IndexedDbRepository from '../db/connection';


class AddPicture extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
      image: "aaa"
    };

    var db, dbtest;
    var uploadingPicture;
  }

  componentDidMount() {

    this.dbtest = new IndexedDbRepository( 'id' );

        this.dbtest.findById( 1 ).then( picture => console.log(picture) );


    }




  render() {



  return(

<div>

<h1>{this.state.name}</h1>

<div className="container-all" id = "maincontent">

  <div className = "showpicture">

  <img src={'data:image/' + this.state.type +';base64,' + btoa(this.state.value)} alt='' />
  </div>
</div>
</div>

);
}

}

export default AddPicture;
