import React from 'react';
import './home.css';

import IndexedDbRepository from '../db/connection';


class Home extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
      postinfo: [],
      urls: [],
      img: null,
    };

    this.uploadpicture = this.uploadpicture.bind(this);
    this.handlePicUpload = this.handlePicUpload.bind(this);

    var db;

    let uploadingPicture =
{

    name: null,
    format: null,
    width: null,
    height: null,
    size: null,
    type: null,
    date: null,
    desc: "test",
    tags: "котики тест проверка",
    value: null
}

  }



  handlePicUpload = (e) => {
  const value = e.target.value;
  this.setState({img: e.target.files[0]});
}


  componentDidMount() {

    this.db = new IndexedDbRepository( 'id' );
    console.log(this.db);
    alert(this.db.resolve);
    }


    uploadpicture()
    {
    //  let uploadingPicture.date = new Date();

    alert(this.db);

		  	let file = this.state.img;
        alert(file);

				var reader = new FileReader();

				reader.readAsBinaryString(file);

				reader.onload = function(e) {
					//alert(e.target.result);
					let bits = e.target.result;
        // uploadingPicture.value = bits;


					let ob = bits;

					let trans = this.db.transaction(['Pictures'], 'readwrite');
					let addReq = trans.objectStore('Pictures').add(ob);

					addReq.onerror = function(e) {
						console.log('error storing data');
						console.error(e);
					}

					trans.oncomplete = function(e) {
						console.log('data stored');
					}
				}

    }


  render() {



  return(

<div>

<h1>Simple gallery pure css</h1>

<div className="container-all" id = "maincontent">

</div>

<div>
    <input type="file" name="image" id="articleimg" class="" accept=".png,.jpg" onChange={this.handlePicUpload}/>
</div>

<button onClick={this.uploadpicture}>  Активировать лазеры

</button>



</div>

);
}

}

export default Home;
