import React from 'react';
import './home.css';

import IndexedDbRepository from '../db/connection';


class AddPicture extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
      name: null,
      desc: null,
      image: false,
      img: [],
      preview: "https://icon-library.net/images/upload-photo-icon/upload-photo-icon-21.jpg"
    };

    this.uploadpicture = this.uploadpicture.bind(this);
    this.handlePicUpload = this.handlePicUpload.bind(this);

    var db, dbtest;
    var uploadingPicture;
  }

  componentDidMount() {

    this.dbtest = new IndexedDbRepository( 'Storage' );
    console.log(this.db);

        this.dbtest.findById( 42 ).then( pictures => this.setState( { pics: pictures } ) );


    }




  render() {



  return(

<div>

<h1>Добавить картинку</h1>

<div className="container-all" id = "maincontent">

  <div className = "uploadingpicture">
    <label for="picture">
    <img src={this.state.preview}/>
    </label>

<input id="picture" type="file" accept="image/*" onChange={this.handlePicUpload} />
  </div>

  <div>
  Title
  <input type="text" required className="form-control" name="name"
    placeholder="Название картинки"
    value={this.state.name}
    onChange={this.handleUserInput}  />
</div>
<br />
<div>
  Text
  <textarea type="textarea" height="300px" className="form-control bigtext" name="desc"
    placeholder="Описание"
    value={this.state.desc}
    onChange={this.handleUserInput}  />
</div>
<button onClick={this.uploadpicture}> Загрузить картинку! </button>

</div>

<div>
</div>






</div>

);
}

}

export default AddPicture;
