import React from 'react';
import './home.css';

import { Redirect } from 'react-router-dom';

import IndexedDbRepository from '../db/connection';


class Edit extends React.Component

{
  constructor(props) {
    super(props);
    this.state = {
      name: null,
      desc: null,
      img: [],
      tags: null,
    };

    this.uploadpicture = this.uploadpicture.bind(this);
    this.colchange = this.colchange.bind(this);

    var db, dbtest;
    var uploadingPicture;
  }


  colchange = (e) =>
  {
    this.uploadingPicture.collection = e.target.value;
  }


handleUserInput = (e) => {
  const name = e.target.name;
  const value = e.target.value;
  this.setState({[name]: value});
}





  componentDidMount() {

  this.dbtest = new IndexedDbRepository( 'Storage' );
  this.dbtest.findById( parseInt(this.props.match.params.PictureID) ).then( picture => {
    this.setState( { name: picture.name });
    this.setState( { desc: picture.desc });
    this.setState( { tags: picture.tags });
}
);

    }

    renderRedirect = () => {
    return <Redirect to='/' />
}


uploadpicture()
{

    if(this.state.name === null) { alert("Введите название картинки!");}
    else{

    this.uploadingPicture.name = this.state.name;
    this.uploadingPicture.desc = this.state.desc;
    this.uploadingPicture.date = new Date();

    if(this.state.tags !== null && this.state.tags !== "")
    {
      this.state.tags = this.state.tags.replace(/\s+/g, ' ').trim();
      if(this.state.tags !== "")
      {
      this.uploadingPicture.tags = this.state.tags.split(" ");
    }
    }

    let file = this.state.img;

    var reader = new FileReader();

    reader.readAsBinaryString(file);

    reader.onload = (e) => {

      let bits = e.target.result;
      this.uploadingPicture.value = bits;

      this.dbtest.save( this.uploadingPicture ).then(function(value) {
        alert("Изображение было добавлено!");
        window.location.href = "/addpicture";
          });

    }
  }
}



  render() {

  return(
<div>

<h1>Редактировать</h1>

<div className="container-all" id = "maincontent">

  <div className = "uploadingpicture">
  <img src={'data:image/' + this.state.image.type +';base64,' + btoa(this.state.image.value)} alt='' />

  </div>

  <div>
  Title
  <input type="text" required className="form-control" name="name"
    placeholder="Название картинки"
    value={this.state.image.name}
    onChange={this.handleUserInput}  />
</div>
<br />
<div>
  Text
  <textarea type="textarea" height="300px" className="form-control bigtext" name="desc"
    placeholder="Описание"
    value={this.state.image.desc}
    onChange={this.handleUserInput}  />
</div>
<button onClick={this.editpicture}> Редактировать! </button>

</div>

  <textarea type="textarea" height="300px" className="form-control bigtext" name="tags"
  placeholder="Теги (через пробел без #) Минимальная длина тега - три символа"
  value={this.state.tags}
  onChange={this.handleUserInput}  />

<div>

<div>
<select onChange = {this.colchange}>
  {
    this.state.collections.map((coll) => {
              return(
                <option>{coll}</option>
    )})
  }

</select>
</div>



</div>
</div>

);
}

}

export default Edit;
